import { animated, useSpring } from '@react-spring/web'

export default function () {
  const [styles, api] = useSpring(
    () => ({
      x: 0,
      rotateZ: 0,
    }),
    []
  )

  const handleClick = () => {
    api.start({
      to: [
        { x: 200, rotateZ: 360 },
        { x: 0, rotateZ: 0 },
      ],
    })
  }

  return (
    <animated.div
      className="spring-box"
      onClick={handleClick}
      style={{
        ...styles,
        cursor: 'pointer',
      }}>
      Click Me!
    </animated.div>
  )
}
